mapper.select:
select temp.*,d.id dId,d.startTime,d.endTime,d.parentId,d.sort from (
select c.id clockId,c.photo clockPhoto,DATE_FORMAT(c.createTime,'%Y-%m-%d %H:%i:%S') clockCreateTime,
c.ipAddress clockIpAddress,c.name clockName,u.id,u.userName,u.age,u.photo,cl.id clId,cl.level
from clock c
LEFT JOIN user u on c.photo = u.photo
LEFT JOIN userlevel ul on ul.userId = u.id
LEFT JOIN clocklevel cl on cl.id = ul.levelId


        and u.userName = #{userName,jdbcType=VARCHAR}


        and DATE_FORMAT(c.createTime,'%Y-%m-%d') >= #{startTime,jdbcType=TIMESTAMP}


        and DATE_FORMAT(c.createTime,'%Y-%m-%d') <= #{endTime,jdbcType=TIMESTAMP}


GROUP BY c.photo,DATE_FORMAT(c.createTime,'%Y-%m-%d')
limit #{startNum},#{endNum}
)temp
LEFT JOIN dimension d on d.parentId = temp.clId
ORDER BY temp.clockCreateTime desc,temp.userName,d.startTime

select count(1) from clock c left join user u on c.photo = u.photo LEFT JOIN userlevel ul on ul.userId = u.id LEFT JOIN clocklevel cl on cl.id = ul.levelId
and u.userName like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
and DATE_FORMAT(c.createTime,'%Y-%m-%d') >= #{startTime,jdbcType=TIMESTAMP}
and DATE_FORMAT(c.createTime,'%Y-%m-%d') <= #{endTime,jdbcType=TIMESTAMP}
GROUP BY c.photo,DATE_FORMAT(c.createTime,'%Y-%m-%d')
